import {ADD_FAV, REMOVE_FAV, FILTER, ORDER} from './actions';

const initialState = {
    myFavorites: [], 
    allCharacters: [],
}

const rootReducer = (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_FAV:
            const addFavoritos = [...state.myFavorites, payload]
            return {
                ...state,
                myFavorites: [...addFavoritos],
                allCharacters: [...addFavoritos]
            }

        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(char => char.id !== payload ) 
            }

        case FILTER:
            return {
                ...state,
                myFavorites: state.allCharacters.filter(char => char.gender === payload),
            }

        case ORDER:
            let orderFavorites;
            if(payload === "A") {
                orderFavorites = state.myFavorites.sort((a,b) => a.id > b.id ? 1 : -1)
            } else {
                orderFavorites = state.myFavorites.sort((a,b) => a.id < b.id ? 1 : -1)
            }
            return {
                ...state,
                myFavorites: [...orderFavorites],
            }

            case 'RESET':
                return {
                    ...state,
                    myFavorites: [...state.allCharacters],
                }

        default:
            return state
    }
}

export default rootReducer;
